public class MyViewModel : INotifyPropertyChanged\n{\n    private bool _allTeams;\n    public bool AllTeams\n    {\n        get { return _allTeams; }\n        set\n        {\n            if (_allTeams != value)\n            {\n                _allTeams = value;\n                OnPropertyChanged("AllTeams");\n                OnPropertyChanged("AllTeamsVisibility");\n            }\n        }\n    }\n\n    public Visibility AllTeamsVisibility\n    {\n        get { return _allTeams ? Visibility.Visible : Visibility.Collapsed; }\n    }\n\n    public event PropertyChangedEventHandler PropertyChanged;\n    protected void OnPropertyChanged(string propertyName)\n    {\n        PropertyChanged?.Invoke(this, new PropertyChangedEventArgs(propertyName));\n    }\n}