using (var context = new YourDbContext())\n{\n    // Step 1: Delete all relationships (ComponentContext entries)\n    var componentContexts = context.ComponentContext.ToList();\n    foreach (var cc in componentContexts)\n    {\n        context.ComponentContext.Remove(cc);\n    }\n    context.SaveChanges();\n    \n    // Step 2: Delete all Context entries\n    var contexts = context.Context.ToList();\n    foreach (var ctx in contexts)\n    {\n        context.Context.Remove(ctx);\n    }\n    context.SaveChanges();\n    \n    // Step 3: Delete all Component entries\n    var components = context.Component.ToList();\n    foreach (var comp in components)\n    {\n        context.Component.Remove(comp);\n    }\n    context.SaveChanges();\n}