using System;\nusing System.ServiceModel;\n\nclass Program\n{\n    static void Main(string[] args)\n    {\n        Uri baseAddress = new Uri("net.tcp://localhost:8080/MyService");\n        NetTcpBinding binding = new NetTcpBinding();\n\n        ChannelFactory<IMyService> factory = new ChannelFactory<IMyService>(binding, new EndpointAddress(baseAddress));\n        IMyService proxy = factory.CreateChannel();\n\n        string response = proxy.SendMessage("Hello, World!");\n        Console.WriteLine("Response from service: " + response);\n\n        ((IClientChannel)proxy).Close();\n        factory.Close();\n    }\n}