using System;\nusing Microsoft.Office.Interop.Access;\nusing System.Runtime.InteropServices;\n\nclass Program\n{\n    static void Main()\n    {\n        string databasePath = @"C:\path\to\your\database.mdb";\n        string oldTableName = "tableName";\n        string newTableName = "newTableName";\n        \n        Application accessApp = new Application();\n        accessApp.OpenCurrentDatabase(databasePath);\n        \n        foreach (AccessObject queryObj in accessApp.CurrentData.AllQueries)\n        {\n            string queryName = queryObj.Name;\n            DoCmd.OpenQuery(queryName, AcView.acViewDesign, AcOpenDataMode.acEdit);\n            QueryDef queryDef = accessApp.CurrentDb().QueryDefs[queryName];\n            string sql = queryDef.SQL;\n            \n            if (sql.Contains(oldTableName))\n            {\n                sql = sql.Replace(oldTableName, newTableName);\n                queryDef.SQL = sql;\n                queryDef.Close();\n                Console.WriteLine($"Updated query: {queryName}");\n            }\n            \n            DoCmd.Close(AcObjectType.acQuery, queryName, AcCloseSave.acSaveYes);\n        }\n        \n        accessApp.CloseCurrentDatabase();\n        Marshal.ReleaseComObject(accessApp);\n        Console.WriteLine("All queries updated.");\n    }\n}