using System;\n\nclass SpiralArray\n{\n    static void Main()\n    {\n        int n = 5; // Size of the array (n x n)\n        int[,] spiralArray = new int[n, n];\n\n        FillSpiralArray(spiralArray, n);\n        PrintArray(spiralArray, n);\n    }\n\n    static void FillSpiralArray(int[,] array, int n)\n    {\n        int value = 1;\n        int top = 0;\n        int bottom = n - 1;\n        int left = 0;\n        int right = n - 1;\n\n        while (value <= n * n)\n        {\n            for (int i = left; i <= right; i++)\n            {\n                array[top, i] = value++;\n            }\n            top++;\n\n            for (int i = top; i <= bottom; i++)\n            {\n                array[i, right] = value++;\n            }\n            right--;\n\n            for (int i = right; i >= left; i--)\n            {\n                array[bottom, i] = value++;\n            }\n            bottom--;\n\n            for (int i = bottom; i >= top; i--)\n            {\n                array[i, left] = value++;\n            }\n            left++;\n        }\n    }\n\n    static void PrintArray(int[,] array, int n)\n    {\n        for (int i = 0; i < n; i++)\n        {\n            for (int j = 0; j < n; j++)\n            {\n                Console.Write(array[i, j] + "\t");\n            }\n            Console.WriteLine();\n        }\n    }\n}