protected void LoginButton_Click(object sender, EventArgs e)\n{\n    string username = UsernameTextBox.Text;\n    string password = PasswordTextBox.Text;\n\n    CustomMembershipProvider customProvider = new CustomMembershipProvider();\n    \n    if (customProvider.ValidateUser(username, password))\n    {\n        // User is authenticated, proceed with login\n        FormsAuthentication.RedirectFromLoginPage(username, RememberMeCheckBox.Checked);\n    }\n    else\n    {\n        // Display an error message\n        FailureText.Text = "Invalid username or password.";\n    }\n}