using System;\nusing System.Web.Security;\n\npublic class CustomMembershipProvider\n{\n    public bool ValidateUser(string username, string password)\n    {\n        // First, check the SQL membership provider\n        if (Membership.Providers["SqlProvider"].ValidateUser(username, password))\n        {\n            return true;\n        }\n        \n        // If not found, check the Active Directory membership provider\n        if (Membership.Providers["ADProvider"].ValidateUser(username, password))\n        {\n            return true;\n        }\n\n        // If neither provider validates the user, return false\n        return false;\n    }\n}