public class HomeController : Controller\n{\n    public ActionResult Index()\n    {\n        return View();\n    }\n\n    public JsonResult GetCountries()\n    {\n        var countries = new List<string> { "USA", "Canada", "UK" };\n        return Json(countries, JsonRequestBehavior.AllowGet);\n    }\n\n    public JsonResult GetStates(string country)\n    {\n        var states = new Dictionary<string, List<string>>\n        {\n            { "USA", new List<string> { "California", "Texas", "New York" } },\n            { "Canada", new List<string> { "Ontario", "Quebec", "British Columbia" } },\n            { "UK", new List<string> { "England", "Scotland", "Wales" } }\n        };\n\n        if (states.ContainsKey(country))\n        {\n            return Json(states[country], JsonRequestBehavior.AllowGet);\n        }\n\n        return Json(new List<string>(), JsonRequestBehavior.AllowGet);\n    }\n}