[AcceptVerbs(HttpVerbs.Post)]\npublic ActionResult Create(FormCollection collection)\n{\n    AppUser newUser = new AppUser();\n\n    try\n    {\n        newUser.UserId = collection["UserId"];\n\n        AppUserDAL.AddUser(newUser);\n\n        return RedirectToAction("Index");\n    }\n    catch (Exception ex)\n    {\n        ViewData["ReturnMessage"] = ex.Message;\n\n        // Ensure the value for UserId is set in the ModelState\n        if (ValueProvider.GetValue("UserId") != null)\n        {\n            ModelState.SetModelValue("UserId", ValueProvider.GetValue("UserId"));\n        }\n\n        ModelState.AddModelError("UserId", "*");\n\n        return View(newUser);\n    }\n}