public interface IRandomNumberGenerator\n{\n    double Generate();\n}\n\n[Export(typeof(IRandomNumberGenerator))]\npublic class ConcreteRNG : IRandomNumberGenerator\n{\n    private double _seed;\n\n    public ConcreteRNG(double seed)\n    {\n        _seed = seed;\n    }\n\n    public double Generate()\n    {\n        // Implement your random number generation logic here\n        return _seed * new Random().NextDouble();\n    }\n}