using System;\nusing System.Collections.Generic;\nusing System.IO;\nusing iText.Forms;\nusing iText.Kernel.Pdf;\n\npublic class PdfFormExtractor\n{\n    public static void Main(string[] args)\n    {\n        string pdfPath = "path/to/your/input.pdf";\n        string csvPath = "path/to/your/output.csv";\n\n        var formData = ExtractFormData(pdfPath);\n        WriteToCsv(formData, csvPath);\n    }\n\n    public static Dictionary<string, string> ExtractFormData(string pdfPath)\n    {\n        var formData = new Dictionary<string, string>();\n\n        using (PdfDocument pdfDoc = new PdfDocument(new PdfReader(pdfPath)))\n        {\n            PdfAcroForm form = PdfAcroForm.GetAcroForm(pdfDoc, false);\n            var fields = form.GetFormFields();\n            \n            foreach (var field in fields)\n            {\n                formData[field.Key] = field.Value.GetValueAsString();\n            }\n        }\n\n        return formData;\n    }\n\n    public static void WriteToCsv(Dictionary<string, string> formData, string csvPath)\n    {\n        using (StreamWriter writer = new StreamWriter(csvPath))\n        {\n            writer.WriteLine("Field Name,Field Value");\n\n            foreach (var field in formData)\n            {\n                writer.WriteLine($"{field.Key},{field.Value}");\n            }\n        }\n    }\n}