using Microsoft.Practices.EnterpriseLibrary.Validation.Validators;\nusing System;\n\n[AttributeUsage(AttributeTargets.Property | AttributeTargets.Field | AttributeTargets.Parameter, AllowMultiple = true, Inherited = false)]\npublic class CustomStringLengthValidatorAttribute : StringLengthValidatorAttribute\n{\n    public CustomStringLengthValidatorAttribute(int upperBound)\n        : base(upperBound)\n    {\n        // Set default values for the message template resource properties\n        this.MessageTemplateResourceName = "InvalidStringLengthMessage";\n        this.MessageTemplateResourceType = typeof(Xltech.Common.Resources.XLStrings);\n    }\n}