public class YourController : Controller\n{\n    private readonly YourDbContext _context;\n\n    public YourController(YourDbContext context)\n    {\n        _context = context;\n    }\n\n    // GET: YourModel/Create\n    public ActionResult Create()\n    {\n        return View();\n    }\n\n    // POST: YourModel/Create\n    [HttpPost]\n    [ValidateAntiForgeryToken]\n    public ActionResult Create(YourModel model)\n    {\n        if (ModelState.IsValid)\n        {\n            // Store the model in TempData to pass it to the confirmation view\n            TempData["ModelData"] = model;\n            return RedirectToAction("ConfirmCreate");\n        }\n\n        return View(model);\n    }\n\n    // GET: YourModel/ConfirmCreate\n    public ActionResult ConfirmCreate()\n    {\n        var model = TempData["ModelData"] as YourModel;\n        \n        if (model == null)\n        {\n            return RedirectToAction("Create");\n        }\n\n        return View(model);\n    }\n\n    // POST: YourModel/ConfirmCreate\n    [HttpPost]\n    [ValidateAntiForgeryToken]\n    public ActionResult ConfirmCreate(YourModel model)\n    {\n        if (ModelState.IsValid)\n        {\n            // Save the model to the database\n            _context.YourModels.Add(model);\n            _context.SaveChanges();\n\n            return RedirectToAction("Index"); // Or any other action\n        }\n\n        return View(model);\n    }\n}