class Program\n{\n    static void Main(string[] args)\n    {\n        // Define the binding (must match the service binding)\n        NetTcpBinding binding = new NetTcpBinding(SecurityMode.None);\n        binding.TransferMode = TransferMode.Buffered;\n        binding.MaxReceivedMessageSize = 65536;\n\n        // Define the endpoint address (must match the service endpoint)\n        EndpointAddress address = new EndpointAddress("net.tcp://localhost:8080/MyService");\n\n        // Create the client and call the service\n        MyServiceClient client = new MyServiceClient(binding, address);\n        string result = client.MyMethod("Hello");\n        Console.WriteLine("Result from service: " + result);\n\n        // Close the client\n        client.Close();\n    }\n}