using System;\nusing System.ComponentModel;\nusing System.Globalization;\nusing System.Windows.Forms;\n\npublic class DateBox : TextBox\n{\n    protected override void OnValidating(CancelEventArgs e)\n    {\n        base.OnValidating(e);\n        \n        if (!IsValidDate(this.Text))\n        {\n            e.Cancel = true;\n            MessageBox.Show("Please enter a valid date in MM/DD/YYYY format.", "Invalid Date", MessageBoxButtons.OK, MessageBoxIcon.Error);\n            this.SelectAll();\n        }\n    }\n\n    private bool IsValidDate(string dateText)\n    {\n        DateTime tempDate;\n        return DateTime.TryParseExact(dateText, "MM/dd/yyyy", CultureInfo.InvariantCulture, DateTimeStyles.None, out tempDate);\n    }\n}