public class FooData {\n    public int Price { get; set; }\n    public int Quantity { get; set; }\n}\n\npublic class FooBusinessObject {\n    public int Price { get; set; }\n    public int Quantity { get; set; }\n\n    public double GetTotal() {\n        return Price * Quantity;\n    }\n}\n\npublic static class FooConverter {\n    public static FooBusinessObject ToBusinessObject(FooData data) {\n        return new FooBusinessObject {\n            Price = data.Price,\n            Quantity = data.Quantity\n        };\n    }\n}\n\n// Usage\nFooData data = new FooData { Price = 10, Quantity = 20 };\nFooBusinessObject busObj = FooConverter.ToBusinessObject(data);\ndouble total = busObj.GetTotal();