using System;\nusing System.Diagnostics;\nusing System.Text.RegularExpressions;\n\nclass Program\n{\n    static void Main()\n    {\n        Trace.Listeners.Add(new ConsoleTraceListener());\n\n        // Example log containing a bell character\n        string log = "This is a test log with a bell char \a in it.";\n\n        // Replace bell character with an empty string or any other character you prefer\n        string sanitizedLog = Regex.Replace(log, "\a", "");\n\n        // Write the sanitized log to the console\n        Trace.WriteLine(sanitizedLog);\n    }\n}