using OpenQA.Selenium;\nusing OpenQA.Selenium.Chrome;\nusing System;\n\nclass Program\n{\n    static void Main()\n    {\n        // Set up the ChromeDriver\n        ChromeOptions options = new ChromeOptions();\n        options.AddArgument("--headless"); // Run in headless mode\n        IWebDriver driver = new ChromeDriver(options);\n\n        try\n        {\n            // Navigate to the webpage\n            driver.Navigate().GoToUrl("http://example.com");\n\n            // Optionally, wait for certain elements to load\n            var wait = new OpenQA.Selenium.Support.UI.WebDriverWait(driver, TimeSpan.FromSeconds(10));\n            wait.Until(d => d.FindElement(By.Id("elementId"))); // Replace with appropriate condition\n\n            // Get the page source or the data you need\n            string pageSource = driver.PageSource;\n            Console.WriteLine(pageSource); // Or process the source as needed\n        }\n        finally\n        {\n            // Clean up\n            driver.Quit();\n        }\n    }\n}