using System;\n\nnamespace ConsoleApplication2\n{\n    class Program\n    {\n        static void Main(string[] args)\n        {\n            try\n            {\n                Type pythonServer = Type.GetTypeFromProgID("PythonDemos.Utilities");\n                if (pythonServer == null)\n                {\n                    throw new Exception("Failed to get Type from ProgID.");\n                }\n                object pythonObject = Activator.CreateInstance(pythonServer);\n                object[] parameters = new object[] { "Hello World", " " };\n                string[] result = (string[])pythonServer.InvokeMember("SplitString", System.Reflection.BindingFlags.InvokeMethod, null, pythonObject, parameters);\n                foreach (var item in result)\n                {\n                    Console.WriteLine(item);\n                }\n            }\n            catch (Exception ex)\n            {\n                Console.WriteLine("Error: " + ex.Message);\n            }\n        }\n    }\n}