using System;\n   using System.Data.SQLite;\n   using Dapper;\n\n   public class Program\n   {\n       public static void Main(string[] args)\n       {\n           string connectionString = "Data Source=MyDatabase.sqlite;Version=3;";\n           using (var connection = new SQLiteConnection(connectionString))\n           {\n               connection.Open();\n\n               // Create a table\n               string createTableQuery = "CREATE TABLE IF NOT EXISTS Users (Id INTEGER PRIMARY KEY AUTOINCREMENT, Name TEXT)";\n               connection.Execute(createTableQuery);\n\n               // Insert a new user\n               string insertQuery = "INSERT INTO Users (Name) VALUES (@Name)";\n               connection.Execute(insertQuery, new { Name = "John Doe" });\n\n               // Query users\n               string selectQuery = "SELECT Id, Name FROM Users";\n               var users = connection.Query<User>(selectQuery);\n\n               foreach (var user in users)\n               {\n                   Console.WriteLine($"ID: {user.Id}, Name: {user.Name}");\n               }\n           }\n       }\n   }\n\n   public class User\n   {\n       public int Id { get; set; }\n       public string Name { get; set; }\n   }