// Create a web service reference to the Lists service\nLists listsService = new Lists();\nlistsService.Url = "http://<sharepoint_site>/_vti_bin/Lists.asmx";\nlistsService.Credentials = System.Net.CredentialCache.DefaultCredentials;\n\n// Call the GetCustomListTemplates method\nXmlNode customListTemplates = listsService.GetCustomListTemplates();\n\n// Display the custom list templates\nforeach (XmlNode customListTemplate in customListTemplates)\n{\n    Console.WriteLine("Title: " + customListTemplate.Attributes["Title"].Value);\n    Console.WriteLine("Name: " + customListTemplate.Attributes["Name"].Value);\n}