using System;\nusing System.Reflection;\nusing System.Runtime.InteropServices;\n\nclass Program\n{\n    static void Main()\n    {\n        Type oClassType = Type.GetTypeFromProgID("GoogleEarth.ApplicationGE");\n        object oGE = Activator.CreateInstance(oClassType);\n\n        object oCamera = oGE.GetType().InvokeMember("GetCamera", BindingFlags.InvokeMethod, null, oGE, new object[] { 0 });\n        double dFocusLatitude = (double)oCamera.GetType().InvokeMember("FocusPointLatitude", BindingFlags.GetProperty, null, oCamera, null);\n        double dFocusLongitude = (double)oCamera.GetType().InvokeMember("FocusPointLongitude", BindingFlags.GetProperty, null, oCamera, null);\n\n        // Release the COM objects\n        if (oCamera != null)\n        {\n            Marshal.ReleaseComObject(oCamera);\n            oCamera = null;\n        }\n        \n        if (oGE != null)\n        {\n            Marshal.ReleaseComObject(oGE);\n            oGE = null;\n        }\n    }\n}