protected void Page_Load(object sender, EventArgs e)\n{\n    if (!IsPostBack)\n    {\n        PopulateMonthDropDown();\n        PopulateYearDropDown();\n    }\n}\n\nprivate void PopulateMonthDropDown()\n{\n    var months = System.Globalization.DateTimeFormatInfo.InvariantInfo.MonthNames\n                 .Where(m => !string.IsNullOrEmpty(m))\n                 .Select((month, index) => new { Value = (index + 1).ToString("D2"), Text = month })\n                 .ToList();\n\n    MonthDropDownList.DataSource = months;\n    MonthDropDownList.DataValueField = "Value";\n    MonthDropDownList.DataTextField = "Text";\n    MonthDropDownList.DataBind();\n}\n\nprivate void PopulateYearDropDown()\n{\n    int startYear = DateTime.Now.Year - 10; // Example: Start 10 years ago\n    int endYear = DateTime.Now.Year + 1; // Example: Up to next year\n\n    var years = Enumerable.Range(startYear, endYear - startYear + 1)\n                          .Select(year => new { Value = year.ToString(), Text = year.ToString() })\n                          .ToList();\n\n    YearDropDownList.DataSource = years;\n    YearDropDownList.DataValueField = "Value";\n    YearDropDownList.DataTextField = "Text";\n    YearDropDownList.DataBind();\n}