using System;\nusing System.Runtime.InteropServices;\nusing Microsoft.Office.Interop.Outlook;\n\npublic class OutlookExample\n{\n    public void UseOutlook()\n    {\n        Application outlookApp = null;\n        NameSpace outlookNamespace = null;\n        MAPIFolder inboxFolder = null;\n\n        try\n        {\n            outlookApp = new Application();\n            outlookNamespace = outlookApp.GetNamespace("MAPI");\n            inboxFolder = outlookNamespace.GetDefaultFolder(OlDefaultFolders.olFolderInbox);\n\n            // Use the COM objects here\n        }\n        finally\n        {\n            if (inboxFolder != null)\n            {\n                Marshal.ReleaseComObject(inboxFolder);\n            }\n            if (outlookNamespace != null)\n            {\n                Marshal.ReleaseComObject(outlookNamespace);\n            }\n            if (outlookApp != null)\n            {\n                Marshal.ReleaseComObject(outlookApp);\n            }\n\n            // Optionally, call GC.Collect to force garbage collection\n            // GC.Collect();\n            // GC.WaitForPendingFinalizers();\n        }\n    }\n}