using System;\nusing System.Data;\nusing System.Data.SqlClient;\nusing System.Text;\n\npublic DataTable SearchRecords(string param1, string param2, string param3, ...)\n{\n    string connectionString = "your_connection_string";\n    \n    StringBuilder query = new StringBuilder("SELECT * FROM table WHERE 1=1");\n    \n    if (!string.IsNullOrEmpty(param1))\n    {\n        query.Append(" AND field1 = @param1");\n    }\n    if (!string.IsNullOrEmpty(param2))\n    {\n        query.Append(" AND field2 = @param2");\n    }\n    if (!string.IsNullOrEmpty(param3))\n    {\n        query.Append(" AND field3 LIKE '%' + @param3 + '%'");\n    }\n    // Add other parameters similarly...\n\n    using (SqlConnection conn = new SqlConnection(connectionString))\n    {\n        using (SqlCommand cmd = new SqlCommand(query.ToString(), conn))\n        {\n            if (!string.IsNullOrEmpty(param1))\n            {\n                cmd.Parameters.AddWithValue("@param1", param1);\n            }\n            if (!string.IsNullOrEmpty(param2))\n            {\n                cmd.Parameters.AddWithValue("@param2", param2);\n            }\n            if (!string.IsNullOrEmpty(param3))\n            {\n                cmd.Parameters.AddWithValue("@param3", param3);\n            }\n            // Add other parameters similarly...\n\n            SqlDataAdapter adapter = new SqlDataAdapter(cmd);\n            DataTable dt = new DataTable();\n            adapter.Fill(dt);\n            return dt;\n        }\n    }\n}