void LinqDataSource_Selecting(object sender, LinqDataSourceSelectEventArgs e)\n{\n    var dataContext = new YourDataContext();\n    IQueryable<YourEntity> query = dataContext.YourEntities.Where(e => e.SomeProperty == someValue);\n\n    // Get the SQL query string\n    string sqlQuery = query.ToString();\n\n    // Set the result to the query\n    e.Result = query;\n\n    // Output or use the SQL query\n    Console.WriteLine(sqlQuery);\n}