using System;\nusing System.Net.Http;\nusing System.Net.Security;\nusing System.Security.Cryptography.X509Certificates;\n\nclass Program\n{\n    static void Main()\n    {\n        var handler = new HttpClientHandler();\n\n        // Set the callback to validate the server certificate\n        handler.ServerCertificateCustomValidationCallback = (HttpRequestMessage httpRequestMessage, X509Certificate2 cert, X509Chain chain, SslPolicyErrors sslPolicyErrors) =>\n        {\n            // Perform your custom certificate validation logic here\n            // Return true to accept the certificate, false to reject it\n            if (sslPolicyErrors == SslPolicyErrors.None)\n            {\n                return true; // valid certificate\n            }\n            else\n            {\n                // Add your custom validation logic if needed\n                Console.WriteLine("Certificate error: {0}", sslPolicyErrors);\n                return false; // invalid certificate\n            }\n        };\n\n        using (var client = new HttpClient(handler))\n        {\n            HttpResponseMessage response = client.GetAsync("https://your-url-here").Result;\n            if (response.IsSuccessStatusCode)\n            {\n                Console.WriteLine("Request succeeded.");\n            }\n            else\n            {\n                Console.WriteLine("Request failed.");\n            }\n        }\n    }\n}