using System;\nusing System.Windows.Forms;\n\nnamespace WebBrowserExample\n{\n    public partial class MainForm : Form\n    {\n        public MainForm()\n        {\n            InitializeComponent();\n            webBrowser1.ObjectForScripting = new ScriptManager(this);\n            webBrowser1.Navigate("path_to_your_login_page.html");\n        }\n\n        public void LoadPdf(string pdfPath)\n        {\n            // Load the PDF using your custom PDF viewer\n            // For example, you might have a method like OpenPdfViewer(pdfPath);\n            MessageBox.Show("PDF Path: " + pdfPath);\n            // OpenPdfViewer(pdfPath); // Your implementation here\n        }\n    }\n\n    [System.Runtime.InteropServices.ComVisible(true)]\n    public class ScriptManager\n    {\n        private MainForm _form;\n\n        public ScriptManager(MainForm form)\n        {\n            _form = form;\n        }\n\n        public void PassPdfPath(string pdfPath)\n        {\n            _form.LoadPdf(pdfPath);\n        }\n    }\n}