using System;\nusing System.IO;\n\nclass Program\n{\n    static void Main()\n    {\n        string configPath = Path.Combine(Environment.GetFolderPath(Environment.SpecialFolder.LocalApplicationData), "YourApp", "config.txt");\n        bool isReadOnlyMode = false;\n\n        if (File.Exists(configPath))\n        {\n            string content = File.ReadAllText(configPath);\n            isReadOnlyMode = content.Contains("ReadOnlyMode=true");\n        }\n\n        if (isReadOnlyMode)\n        {\n            Console.WriteLine("Starting in read-only mode.");\n            // Start application in read-only mode\n        }\n        else\n        {\n            Console.WriteLine("Starting in normal mode.");\n            // Start application in normal mode\n        }\n    }\n}