public DataTable ConvertUtcToLocal(DataTable dataTable)\n{\n    DataTable newTable = dataTable.Clone(); // Clone the structure of the original table\n\n    foreach (DataRow row in dataTable.Rows)\n    {\n        DataRow newRow = newTable.NewRow();\n        foreach (DataColumn column in dataTable.Columns)\n        {\n            if (column.DataType == typeof(DateTime))\n            {\n                DateTime utcDate = (DateTime)row[column];\n                DateTime localDate = TimeZoneInfo.ConvertTimeFromUtc(utcDate, TimeZoneInfo.Local);\n                newRow[column.ColumnName] = localDate;\n            }\n            else\n            {\n                newRow[column.ColumnName] = row[column];\n            }\n        }\n        newTable.Rows.Add(newRow);\n    }\n\n    return newTable;\n}