using System;\nusing System.Runtime.InteropServices;\n\nclass Program\n{\n    [DllImport("User32.dll")]\n    public static extern IntPtr FindWindow(string lpClassName, string lpWindowName);\n\n    [DllImport("user32.dll")]\n    public static extern IntPtr FindWindowEx(IntPtr hwndParent, IntPtr hwndChildAfter, string lpszClass, string lpszWindow);\n\n    [DllImport("User32.dll")]\n    public static extern int SendMessage(IntPtr hWnd, int uMsg, int wParam, string lParam);\n\n    const int WM_SETTEXT = 0x000C;\n\n    static void Main(string[] args)\n    {\n        string textToSend = "Hello, mIRC!";\n\n        IntPtr mainHandle = FindWindow("mIRC", null);\n        if (mainHandle == IntPtr.Zero)\n        {\n            Console.WriteLine("mIRC window not found.");\n            return;\n        }\n\n        IntPtr serverHandle = FindWindowEx(mainHandle, IntPtr.Zero, "MDIClient", null);\n        if (serverHandle == IntPtr.Zero)\n        {\n            Console.WriteLine("MDIClient window not found.");\n            return;\n        }\n\n        IntPtr chanHandle = FindWindowEx(serverHandle, IntPtr.Zero, null, null);\n        if (chanHandle == IntPtr.Zero)\n        {\n            Console.WriteLine("Channel window not found.");\n            return;\n        }\n\n        IntPtr editHandle = FindWindowEx(chanHandle, IntPtr.Zero, "Edit", null);\n        if (editHandle == IntPtr.Zero)\n        {\n            Console.WriteLine("Edit control not found.");\n            return;\n        }\n\n        SendMessage(editHandle, WM_SETTEXT, 0, textToSend);\n        Console.WriteLine("Text sent to mIRC.");\n    }\n}