using System;\nusing System.Collections.Generic;\nusing System.Diagnostics;\nusing System.Linq;\n\npublic class Program\n{\n    public static void Main()\n    {\n        List<char> letterRange = Enumerable.Range('a', 'z' - 'a' + 1)\n                                           .Select(i => (char)i)\n                                           .ToList();\n\n        Stopwatch stopwatch = new Stopwatch();\n\n        // Standard foreach\n        stopwatch.Start();\n        foreach (var range in letterRange)\n        {\n            Console.Write(range + ",");\n        }\n        Console.Write("\n");\n        stopwatch.Stop();\n        Console.WriteLine($"Standard foreach: {stopwatch.ElapsedTicks} ticks");\n\n        // Inbuilt ForEach\n        stopwatch.Restart();\n        letterRange.ForEach(range => Console.Write(range + ","));\n        Console.Write("\n");\n        stopwatch.Stop();\n        Console.WriteLine($"Inbuilt ForEach: {stopwatch.ElapsedTicks} ticks");\n\n        // for loop\n        stopwatch.Restart();\n        for (int i = 0; i < letterRange.Count; i++)\n        {\n            Console.Write(letterRange[i] + ",");\n        }\n        Console.Write("\n");\n        stopwatch.Stop();\n        Console.WriteLine($"for loop: {stopwatch.ElapsedTicks} ticks");\n    }\n}