using System;\nusing SHDocVw;\nusing mshtml;\n\nnamespace IEAutomation\n{\n    class Program\n    {\n        static void Main(string[] args)\n        {\n            InternetExplorer ie = new InternetExplorer();\n            ie.Visible = true;\n\n            // Navigate to a webpage\n            ie.Navigate("http://example.com");\n\n            // Wait for the page to load\n            while (ie.ReadyState != tagREADYSTATE.READYSTATE_COMPLETE)\n            {\n                System.Threading.Thread.Sleep(100);\n            }\n\n            // Access the document\n            HTMLDocument document = (HTMLDocument)ie.Document;\n\n            // Find a button by its ID and click it\n            IHTMLElement button = document.getElementById("buttonId");\n            if (button != null)\n            {\n                button.click();\n            }\n\n            // Set the value of a text input field by its ID\n            IHTMLInputElement inputField = (IHTMLInputElement)document.getElementById("inputFieldId");\n            if (inputField != null)\n            {\n                inputField.value = "New Value";\n            }\n\n            // Read the value of a text input field by its ID\n            string inputValue = inputField?.value;\n            Console.WriteLine($"Input Field Value: {inputValue}");\n\n            // Close the IE instance\n            ie.Quit();\n        }\n    }\n}