using System;\nusing System.Configuration;\nusing System.IO;\nusing System.Net;\n\nnamespace YourNamespace\n{\n    public class Form : System.Windows.Forms.Form\n    {\n        public IPAddress IP { get; private set; }\n\n        public Form()\n        {\n            InitializeComponent();\n            IP = GetIPAddressFromConfig("YourDll.config");\n        }\n\n        private IPAddress GetIPAddressFromConfig(string configFileName)\n        {\n            string exePath = AppDomain.CurrentDomain.BaseDirectory;\n            string configFilePath = Path.Combine(exePath, configFileName);\n\n            if (File.Exists(configFilePath))\n            {\n                ExeConfigurationFileMap configMap = new ExeConfigurationFileMap();\n                configMap.ExeConfigFilename = configFilePath;\n                Configuration config = ConfigurationManager.OpenMappedExeConfiguration(configMap, ConfigurationUserLevel.None);\n\n                string ipAddress = config.AppSettings.Settings["IPAddress"].Value;\n                return IPAddress.Parse(ipAddress);\n            }\n            else\n            {\n                throw new FileNotFoundException("Config file not found");\n            }\n        }\n    }\n}