using System;\nusing System.Collections.Generic;\nusing System.Web.Services;\nusing System.Xml.Serialization;\n\n[Serializable]\npublic class StorageObject\n{\n    public string Name { get; set; }\n    public string Birthday { get; set; }\n\n    [XmlIgnore]\n    public Dictionary<string, string> OtherInfo { get; set; }\n\n    [XmlElement("OtherInfo")]\n    public NameValuePairs[] OtherInfoSerializable\n    {\n        get\n        {\n            var list = new List<NameValuePairs>();\n            foreach (var kvp in OtherInfo)\n            {\n                list.Add(new NameValuePairs { Name = kvp.Key, Value = kvp.Value });\n            }\n            return list.ToArray();\n        }\n        set\n        {\n            OtherInfo = new Dictionary<string, string>();\n            foreach (var pair in value)\n            {\n                OtherInfo[pair.Name] = pair.Value;\n            }\n        }\n    }\n}\n\n[Serializable]\npublic class NameValuePairs\n{\n    public string Name { get; set; }\n    public string Value { get; set; }\n}\n\n[WebService]\npublic class MyWebService : WebService\n{\n    [WebMethod]\n    public List<StorageObject> GetStorageObjects()\n    {\n        var storageObjects = new List<StorageObject>\n        {\n            new StorageObject\n            {\n                Name = "Matthew",\n                Birthday = "Jan 1st, 2008",\n                OtherInfo = new Dictionary<string, string>\n                {\n                    { "Hobbies", "Programming" },\n                    { "Website", "Stackoverflow.com" }\n                }\n            },\n            new StorageObject\n            {\n                Name = "Joe",\n                Birthday = "Jan 10th, 2008",\n                OtherInfo = new Dictionary<string, string>\n                {\n                    { "Hobbies", "Programming" },\n                    { "Website", "Stackoverflow.com" }\n                }\n            }\n        };\n\n        return storageObjects;\n    }\n}