protected void SaveButton_Click(object sender, EventArgs e)\n{\n    if (ValidateControls())\n    {\n        // Proceed with saving the data\n    }\n    else\n    {\n        // Handle the validation failure\n        lblError.Text = "Each Control1 must have at least one Control2.";\n    }\n}\n\nprivate bool ValidateControls()\n{\n    bool isValid = true;\n\n    foreach (Control control1 in Page.Controls)\n    {\n        if (control1 is Control1)\n        {\n            bool hasControl2 = false;\n\n            foreach (Control childControl in control1.Controls)\n            {\n                if (childControl is Control2)\n                {\n                    hasControl2 = true;\n                    break;\n                }\n            }\n\n            if (!hasControl2)\n            {\n                isValid = false;\n                break;\n            }\n        }\n    }\n\n    return isValid;\n}