using System;\nusing System.Text;\n\npublic class SmsEncoder\n{\n    public static string EncodeToGsm0338(string message)\n    {\n        // GSM 03.38 encoding table\n        var gsm0338Encoding = new Dictionary<char, byte>\n        {\n            { '£', 0xA3 }\n            // Add other characters as needed\n        };\n\n        var encodedMessage = new StringBuilder();\n        foreach (char c in message)\n        {\n            if (gsm0338Encoding.ContainsKey(c))\n            {\n                encodedMessage.Append(gsm0338Encoding[c].ToString("X2"));\n            }\n            else\n            {\n                // Default to GSM 03.38 basic character set or handle as needed\n                encodedMessage.Append(((byte)c).ToString("X2"));\n            }\n        }\n\n        return encodedMessage.ToString();\n    }\n\n    public static void Main()\n    {\n        string message = "This is a test message with £ symbol.";\n        string encodedMessage = EncodeToGsm0338(message);\n\n        Console.WriteLine("Original Message: " + message);\n        Console.WriteLine("Encoded Message: " + encodedMessage);\n    }\n}