using System.Web;\nusing System.Web.SessionState;\n\npublic class CustomSessionIDManager : SessionIDManager\n{\n    public override string CreateSessionID(HttpContext context)\n    {\n        string baseSessionId = base.CreateSessionID(context);\n        string companyName = GetCompanyNameFromUrl(context.Request.Url);\n        return $"{companyName}_{baseSessionId}";\n    }\n\n    public override bool Validate(string id)\n    {\n        // Assuming company names do not contain underscores.\n        string[] parts = id.Split('_');\n        if (parts.Length != 2)\n            return false;\n        return base.Validate(parts[1]);\n    }\n\n    private string GetCompanyNameFromUrl(Uri url)\n    {\n        // Extract the company name from the URL.\n        // This assumes URLs are in the format: /companyName/...\n        string[] segments = url.Segments;\n        return segments.Length > 1 ? segments[1].Trim('/') : string.Empty;\n    }\n}