var sizes = (from t in table\n             where t.ID == 1\n             group t by new {\n                 t.Length,\n                 t.Width\n             } into g\n             orderby g.Key.Length, g.Key.Width\n             select new {\n                 Size = g.Key.Length + " x " + g.Key.Width\n             })\n            .ToList()\n            .Select(x => x.Size)\n            .Aggregate((current, next) => current + ", " + next);\n\nConsole.WriteLine("SIZES\n=================\n" + sizes);