using System;\nusing System.Globalization;\nusing System.Threading;\nusing System.Windows.Forms;\n\nnamespace LocalizationLauncher\n{\n    static class Program\n    {\n        [STAThread]\n        static void Main(string[] args)\n        {\n            // Check if a culture is passed as an argument\n            if (args.Length > 0)\n            {\n                try\n                {\n                    // Set the culture based on the argument\n                    CultureInfo culture = new CultureInfo(args[0]);\n                    Thread.CurrentThread.CurrentCulture = culture;\n                    Thread.CurrentThread.CurrentUICulture = culture;\n                }\n                catch (CultureNotFoundException)\n                {\n                    MessageBox.Show("Invalid culture specified.", "Error", MessageBoxButtons.OK, MessageBoxIcon.Error);\n                    return;\n                }\n            }\n\n            Application.EnableVisualStyles();\n            Application.SetCompatibleTextRenderingDefault(false);\n            Application.Run(new MainForm()); // Replace MainForm with your form class\n        }\n    }\n}