using System.Web.Http;\n\npublic class AccountsController : ApiController\n{\n    [HttpGet]\n    [Route("accounts/{id}")]\n    public IHttpActionResult GetAccount(string id)\n    {\n        // Read the HTML file\n        string html = System.IO.File.ReadAllText(HttpContext.Current.Server.MapPath("~/accounts/accounts.htm"));\n        \n        // Replace the token\n        html = html.Replace("{id}", id);\n\n        // Return the response\n        return new HtmlResult(html, Request);\n    }\n}\n\npublic class HtmlResult : IHttpActionResult\n{\n    private readonly string _html;\n    private readonly HttpRequestMessage _request;\n\n    public HtmlResult(string html, HttpRequestMessage request)\n    {\n        _html = html;\n        _request = request;\n    }\n\n    public Task<HttpResponseMessage> ExecuteAsync(CancellationToken cancellationToken)\n    {\n        var response = new HttpResponseMessage(HttpStatusCode.OK)\n        {\n            Content = new StringContent(_html)\n        };\n        response.Content.Headers.ContentType = new MediaTypeHeaderValue("text/html");\n        return Task.FromResult(response);\n    }\n}