// ParentPage.aspx.cs\npublic partial class ParentPage : System.Web.UI.Page\n{\n    protected void Page_Load(object sender, EventArgs e)\n    {\n    }\n\n    protected void Page_Init(object sender, EventArgs e)\n    {\n        // Find the child control and attach event handler\n        ChildControl childControl = (ChildControl)FindControl("ChildControlID");\n        if (childControl != null)\n        {\n            childControl.EditEvent += new ChildControl.EditEventHandler(OnEditEvent);\n        }\n    }\n\n    protected void OnEditEvent(object sender, EventArgs e)\n    {\n        // Handle the event and modify parent page properties\n        EditControl.Visible = true;\n        ListControl.Visible = false;\n        AddControl.Visible = false;\n\n        EditControl.Enabled = true;\n        ListControl.Enabled = false;\n        AddControl.Enabled = false;\n    }\n}