var stuff = from l in _db.SqlLinks\n            select new\n            {\n                Link = l,\n                Rating = (from v in l.SqlLinkVotes\n                          where v.Tag == tagId\n                                && v.VoteDate >= since\n                          select (int?)v.Vote).Sum() ?? 0,\n                NumberOfVotes = (from v in l.SqlLinkVotes\n                                 where v.Tag == tagId\n                                       && v.VoteDate >= since\n                                 select v.Vote).Count(),\n                NumberOfComments = (from v in l.SqlLinkVotes\n                                    where v.Tag == tagId\n                                          && v.VoteDate >= since\n                                          && v.Comment != ""\n                                    select v.Vote).Count()\n            };