using System;\nusing System.Data.SqlClient;\n\npublic class DatabaseHelper\n{\n    private const int SqlUniqueConstraintViolationErrorCode = 2627; // Error code for unique constraint violation\n\n    public void ExecuteStoredProcedure()\n    {\n        try\n        {\n            // Your code to execute the stored procedure\n        }\n        catch (SqlException ex)\n        {\n            if (ex.Number == SqlUniqueConstraintViolationErrorCode)\n            {\n                // Handle unique constraint violation\n                throw new UniqueConstraintViolationException("A unique constraint was violated.", ex);\n            }\n            else\n            {\n                // Re-throw the original exception\n                throw;\n            }\n        }\n    }\n}\n\npublic class UniqueConstraintViolationException : Exception\n{\n    public UniqueConstraintViolationException(string message, Exception innerException)\n        : base(message, innerException)\n    {\n    }\n}