using MapPoint;\n\nclass Program\n{\n    static void Main()\n    {\n        // Initialize MapPoint application\n        Application mappointApp = new Application();\n        mappointApp.Visible = true;\n        Map map = mappointApp.ActiveMap;\n\n        // Find the place (e.g., a specific city)\n        FindResults results = map.FindPlaceResults("West Bend, WI");\n        if (results.Count > 0)\n        {\n            Location location = results[1] as Location;\n\n            // Define the place category for Police Stations\n            PlaceCategory policeStationsCategory = map.PlaceCategories["Police Stations"];\n\n            // Find nearby places of the specified category within a certain radius (e.g., 10 miles)\n            FindResults nearbyResults = location.FindNearby(10, policeStationsCategory);\n\n            // Iterate over the results and list the police stations\n            foreach (Location loc in nearbyResults)\n            {\n                Console.WriteLine(loc.Name);\n            }\n        }\n        else\n        {\n            Console.WriteLine("Location not found.");\n        }\n\n        // Cleanup\n        mappointApp.Quit();\n    }\n}