public static void Call_SS_Initialize()\n{\n    Int16 ret;\n    UInt16[] serverIds = new UInt16[] { 2020, 2021 };\n    StringBuilder binding = new StringBuilder(256); // Initialize with a sufficient buffer size\n    StringBuilder logPath = new StringBuilder(256); // Initialize with a sufficient buffer size\n\n    try\n    {\n        ret = SS_Initialize(\n            serverIds,\n            Convert.ToUInt16(serverIds.Length),\n            binding,\n            logPath,\n            10,\n            0,\n            256,\n            300,\n            ProcessMessage);\n\n        Console.WriteLine("Call_SS_Initialize() -> Result of SS_Initialize: {0}", ret.ToString());\n    }\n    catch (Exception ex)\n    {\n        Int32 err = Marshal.GetLastWin32Error();\n        throw new Win32Exception(err);\n    }\n}