[ImportMany]\nprivate IEnumerable<Lazy<ExtendedArtifactBase, IDictionary<string, object>>> _applications { get; set; }\n\npublic ExtendedArtifactBase GetApplication(string applicationName)\n{\n    var application = _applications\n        .Where(app => app.Metadata.ContainsKey("Application") && \n                      app.Metadata["Application"].ToString() == applicationName)\n        .Select(app => app.Value)\n        .FirstOrDefault();\n\n    return application;\n}