protected override void OnKeyPress(KeyPressEventArgs e)\n{\n    // Allow Backspace\n    if (e.KeyChar == (char)Keys.Back)\n    {\n        base.OnKeyPress(e);\n        return;\n    }\n\n    // Allow Ctrl combinations (Ctrl-C, Ctrl-V, etc.)\n    if (Control.ModifierKeys == Keys.Control)\n    {\n        base.OnKeyPress(e);\n        return;\n    }\n\n    // Regular expression to check for valid characters\n    string validCharExpression = "[A-Za-z]"; // Example regex for alpha characters only\n\n    if (String.IsNullOrEmpty(validCharExpression))\n    {\n        base.OnKeyPress(e);\n    }\n    else\n    {\n        bool isValidChar = Regex.Match(e.KeyChar.ToString(), validCharExpression).Success;\n        if (isValidChar)\n        {\n            base.OnKeyPress(e);\n        }\n        else\n        {\n            e.Handled = true;\n        }\n    }\n}