// ASPX Page\n<asp:Label ID="Label1" runat="server" Text="Original Text"></asp:Label>\n<asp:HiddenField ID="OriginalTextHidden" runat="server" />\n\n// Code-behind (C#)\nprotected void Page_Load(object sender, EventArgs e)\n{\n    if (!IsPostBack)\n    {\n        // Store the original text in a hidden field or ViewState\n        OriginalTextHidden.Value = Label1.Text;\n    }\n}\n\nprotected string GetOriginalLabelText()\n{\n    // Retrieve the original text from the hidden field\n    return OriginalTextHidden.Value;\n}\n\n// Usage example\nprotected void SomeButton_Click(object sender, EventArgs e)\n{\n    string originalText = GetOriginalLabelText();\n    // Do something with originalText\n}